package week2.day2;

public class ConvertNegativeToPositive {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number=-78;
		if(number<0)
		{
		
			System.out.println("The Number " +number+ " is converted to "  +(number*(-1)) );
		}
		else {
			System.out.println("given number is not a negative number");
		}
	}

}
