package week2.day2;

public class EvenOrOld {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=10;
		for(int i=0;i<=10;i++)
		{
			if(i%2==0)
			{
				System.out.println( i+ " is even number" );
			}else{
				System.out.println( i+ "is odd number");
			}
		}
	}

}
